var errorQueue = []; 
var timeout;

function isBot() {
	const bots = ['bot', 'googlebot', 'bingbot', 'facebook', 'slurp', 'twitter','yahoo']; // Adicione outros bots, se necessÃ¡rio
	const userAgent = navigator.userAgent.toLowerCase();
	return bots.some(bot => userAgent.includes(bot));
}


window.onerror = function(msg, url, line) {
	var errorMessage = [
		'Message: ' + msg,
		'URL: ' + url,
		'Line: ' + line
	].join(' - ');

	// Filter bots errors...
	if (isBot()) {
		return;
	}
	
errorQueue.push(errorMessage); 
if (errorQueue.length >= 5) { 
	sendErrorsToServer();
} else {
	clearTimeout(timeout);
	timeout = setTimeout(sendErrorsToServer, 5000); 
}
}
function sendErrorsToServer() {
if (errorQueue.length > 0) {
var message = errorQueue.join(' | ');
//console.log(message);
var xhr = new XMLHttpRequest();
var nonce = 'ce961f5da5';
// var ajaxurl = encodeURIComponent('https://hammerandnailsgrooming.com/wp-admin/admin-ajax.php?action=log_js_error&_wpnonce=ce961f5da5');
var ajaxurl = 'https://hammerandnailsgrooming.com/wp-admin/admin-ajax.php?action=log_js_error&_wpnonce=ce961f5da5';
xhr.open('POST', encodeURI(ajaxurl)); 
xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
xhr.onload = function() {
	if (200 === xhr.status) {
	   try {
		  //console.log(xhr.response);
	   } catch (e) {
		   console.log('error xhr not 200!');
	   }
	} else
	   {
		console.log('error 2');
	}
};
xhr.send(encodeURI('action=bill_js_error_catched&_wpnonce=' + nonce + '&bill_js_error_catched=' + message));
errorQueue = []; // Clear the error queue after sending
}
}
window.addEventListener('beforeunload', sendErrorsToServer);
