function getLatLngByZipcode() 
{
    let address = jQuery('.input-zipcode').val();
	let form = new FormData();
	const url = 'https://hammerandnailsgrooming.com/wp-admin/admin-ajax.php';
	const nonce = '583bf7f7ec';
	
	form.append('address',address);
	form.append('action','hammernails_map_cache');
	form.append('nonce',nonce)

	fetch(url,{
      method: 'post',
      body:form
    }).then(function(res){return res.text();}).then(function(res){
		var resData = JSON.parse(res);
		console.log('CACHE DATA ***********');
      	console.log(resData);
		if(resData.result==false){
			var geocoder = new google.maps.Geocoder();
			geocoder.geocode({ 'address': address + ' zip' }, function (results, status) {
				console.log(results);
				if (status == google.maps.GeocoderStatus.OK) {
					var latitude = results[0].geometry.location.lat();
					var longitude = results[0].geometry.location.lng();
					console.log("Latitude: " + latitude + "\nLongitude: " + longitude);
					let save = new FormData();
					
					save.append('address',address);
					save.append('lat_lng',latitude+','+longitude);
					save.append('action','hammernails_map_save_cache');

					fetch(url,{
						method: 'post',
						body:save
					}).then(function(res){return res.text()}).then(function(saveRes){
						let saveResData = JSON.parse(saveRes);

						console.log('*****SAVE RES DATA*******',saveResData);
						jQuery.ajax({
							type : "POST",
							url : "https://hammerandnailsgrooming.com/wp-admin/admin-ajax.php",
							data : {action: "hammernails_distance_locations_new",
									latitude:latitude,
									longitude:longitude    },
							success: function(response) {
								
								//console.log(response=='');
								if(response!=''){
									// console.log('responses found');
									jQuery('#myModal_loc .location').html(response);
								} else {
									//  					   console.log('responses not found');
									var notFound = '<div class="col-md-12"><div class="text-center"><p style="color:#fff;">There are no shops near you. Interested in opening a Franchise?</p><div class="col-md-6 offset-md-4 offset-xs-5"><a class="Hlink text-center" href="https://hammerandnailsfranchise.com/" target="_blank"><span>Learn More</span></a></div></div></div>';
									//console.log(notFound);
									jQuery('#myModal_loc .location').html(notFound);
								}
								//                   $('#myModal_loc .location').html(response);

								jQuery('#myModal_loc').show();

							}
						});
					});
				} else {
					console.log(status);
					var notFound = '<div class="col-md-12"><div class="text-center"><p style="color:#fff;">Unfortunately, there is not a Hammer & Nails location near you.</p><div class="col-md-6 offset-md-4 offset-xs-5"><a class="Hlink text-center" href="/our-locations/" target=""><span>View All Locations</span></a></div></div></div>';
									//
							jQuery('#myModal_loc .location').html(notFound);
							jQuery('#myModal_loc').show();
				}
			});




		} else {
			let latitude = resData.result.split(',')[0];
			let longitude = resData.result.split(',')[1];
			jQuery.ajax({
				type : "POST",
				url : "https://hammerandnailsgrooming.com/wp-admin/admin-ajax.php",
				data : {action: "hammernails_distance_locations_new",
						latitude:latitude,
						longitude:longitude    },
				success: function(response) {
					
					//console.log(response=='');
					if(response!=''){
						// console.log('responses found');
						jQuery('#myModal_loc .location').html(response);
					} else {
						//  					   console.log('responses not found');
						var notFound = '<div class="col-md-12"><div class="text-center"><p style="color:#fff;">There are no shops near you. Interested in opening a Franchise?</p><div class="col-md-6 offset-md-4 offset-xs-5"><a class="Hlink text-center" href="https://hammerandnailsfranchise.com/" target="_blank"><span>Learn More</span></a></div></div></div>';
						//console.log(notFound);
						jQuery('#myModal_loc .location').html(notFound);
					}
					//                   $('#myModal_loc .location').html(response);

					jQuery('#myModal_loc').show();

				}
			});
		}
	});
}

